jQuery(document).ready(function($){const{select,dispatch}=window.cf.vendor["@wordpress/data"];const metaboxes=select("carbon-fields/metaboxes");const{updateFieldValue}=dispatch("carbon-fields/metaboxes");const fields=metaboxes.getFields();const buttons=["update_language","create_pages","delete_pages"];for(let i=0;i<buttons.length;i++){$("#mds_"+buttons[i]).click(function(e){e.preventDefault();e.stopPropagation();$(this).prop("disabled",true);const button=$(this);button.css("background-color","#663600");$.post(MDS.ajaxurl,{action:"mds_"+buttons[i],nonce:MDS.nonce},function(data){$(this).prop("disabled",false);if(data){button.css("background-color","#006606")}else{button.css("background-color","#660000")}button.delay(5e3).queue(function(next){button.css("background-color","");next()});const pages=JSON.parse(MDS.pages);for(let page in pages){const option=pages[page]["option"];let field=Object.values(fields).find(f=>f.base_name===MDS.MDS_PREFIX+option);updateFieldValue(field.id,data[option])}})})}});